package Homework5;

public class assessmentOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int N = 1;
		
		while (N <= 20)
		{
			System.out.println("print number    "+N);
			N++;
		}
		
		
		
		int num = 1;
		
		do
		{
			System.out.println("print number   "+num);
			num++;
		}while (num <= 20);
		
		
		
		for (int a = 1; a<=20; a++)
		{
			System.out.println("print number    "+a);
		}
		
		

	}

}
